不太想用判断，写起来太繁琐了。
    private static void test(string str)
    {
        Console.WriteLine(str);
    }

    private static void test(int i)
    {
        Console.WriteLine(i);
    }

    private static void Main(string[] args)
    {
        var a = new List<object>() { 123, "asd" };
        test(a[0]);
        test(a[1]);
    }
