class Timer1 : Timer{
    override fun delay(long: Long, block: () -> Long) {
        handler.postDelayed({
            val seconds = block()
            print(seconds)
        }, long)
    }
}

‰ªé Timer1 ÁöÑÂÆûÁé∞ÂèØ‰ª•ÂæóÁü•ÔºåÊó∂Èó¥Èó¥ÈöîÊòØÊØ´ÁßíÔºåËÄå block ËøîÂõûÂÄºÊòØÁßí„ÄÇ
‰ΩÜÈ°πÁõÆ‰∏≠ÂèØËÉΩÂêåÊó∂Â≠òÂú®‰∏ãÈù¢ËøôÊ†∑ÁöÑÂÆûÁé∞Ôºö
class Timer2 : Timer {
    override fun delay(long: Long, block: () -> Long) {
        GlobalScope.launch {
            delay(long.toDuration(DurationUnit.SECONDS))
            val milliseconds = block()
            print(milliseconds)
        }
    }
}

